using System; class Program { static void Main() { int n = 5; // Altura da árvore de Natal for (int i = 0; i < n; i++) { for (int j = n - i; j > 1; j--) {Console.Write(" "); } for (int j = 0; j <= i; j++) { Console.Write("* "); } Console.WriteLine(); } // Tronco da árvore for (int i = 0; i < n - 2; i++) { Console.Write(" "); } Console.WriteLine("| |"); } }
